#include "jikka/not_modulo.hpp"
#include <algorithm>
#include <array>
#include <cassert>
#include <cstdint>
#include <functional>
#include <iostream>
#include <numeric>
#include <string>
#include <tuple>
#include <vector>
int64_t solve(int64_t h_1575, int64_t w_1576, int64_t k_1577) {
  int64_t x1578 = 0;
  for (int32_t x1579 = 0; x1579 < h_1575; ++x1579) {
    int64_t x1582 = x1578;
    for (int32_t x1583 = 0; x1583 < w_1576; ++x1583) {
      for (int32_t x1587 = 0; x1587 < h_1575; ++x1587) {
        for (int32_t x1591 = 0; x1591 < w_1576; ++x1591) {
          x1582 = std::array<int64_t, 2>{x1579, x1583} <
                          std::array<int64_t, 2>{x1587, x1591}
                      ? x1582 +
                            std::abs(-x1579 + x1587) *
                                jikka::notmod::choose(h_1575 * w_1576 - 2,
                                                      k_1577 - 2) +
                            std::abs(-x1583 + x1591) *
                                jikka::notmod::choose(h_1575 * w_1576 - 2,
                                                      k_1577 - 2)
                      : x1582;
        }
      }
    }
    x1578 = jikka::modmat::floormod<1>(std::array<int64_t, 1>{x1582},
                                       1000000007)[0];
  }
  return x1578;
}
int main() {
  int64_t n_1594 = -1;
  int64_t m_1595 = -1;
  int64_t k_1596 = -1;
  std::cin >> n_1594;
  std::cin >> m_1595;
  std::cin >> k_1596;
  auto ans_1597 = solve(n_1594, m_1595, k_1596);
  std::cout << ans_1597 << ' ';
  std::cout << '\n' << ' ';
}

